        sql_object_count=attrs.sql_object_count,
        alias=attrs.alias,
        is_temporary=attrs.is_temporary,
        is_query_preview=attrs.is_query_preview,
        query_preview_config=attrs.query_preview_config,
        external_location=attrs.external_location,
        external_location_region=attrs.external_location_region,
        external_location_format=attrs.external_location_format,
        is_partitioned=attrs.is_partitioned,
        partition_strategy=attrs.partition_strategy,
